prepare_command:
find -E . -type f -not -name 'RAC*' -regex '.*(EXT.*|metamacros)\.[hm]$' \
          -execdir mv '{}' RAC'{}' \;
find . -regex '.*\.[hm]' \
       -exec perl -pi \
                  -e 's@"(?:(?!RAC)(EXT.*|metamacros))\.h"@"RAC\1.h"@' '{}' \;
find . -regex '.*\.[hm]' \
       -exec perl -pi \
                  -e 's@<ReactiveObjC/(?:(?!RAC)(EXT.*))\.h>@<ReactiveObjC/RAC\1.h>@' '{}' \;
